// PopUpCart.js
import React, {useState, useEffect, useMemo} from 'react';
import './PopUpCart.css'; // Create this CSS file for styling

const PopUpCart = ({ cartItems,  goToCart }) => {
  // const [isVisible, setIsVisible] = useState(false);
  const [isExpanded, setIsExpanded] = useState(false); // Controls the expanded state of the popup
  // You can include more logic to handle clicking on the cart

  const totalQuantity = useMemo(() => {return cartItems.reduce((total, item) => total + (item.quantity), 0)},[cartItems])


  return (
    <div className="floating-cart">

      <span className="item-count-badge">{totalQuantity}</span>
      <span className="fas fa-shopping-cart" onClick={goToCart}>{totalQuantity} dishes have been ordered</span> {/* Font Awesome cart icon */}

      <span>Settle Payment</span>


    </div>
  );
};

export default PopUpCart;
