import React from 'react';
import './PaymentSuccessfulPage.css';


const PaymentSuccessfulPage = ({ cartItems, roomNumber, orderNumber, goBackToCart }) => {
  // Assume serviceCharge and tax are percentages (e.g., 5% service charge, 10% tax)
  const serviceChargeRate = 0.05;
  const taxRate = 0.10;

  // Calculate subtotal
  const subtotal = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  // Calculate service charge and tax
  const serviceCharge = subtotal * serviceChargeRate;
  const tax = subtotal * taxRate;

  // Calculate total
  const total = subtotal + serviceCharge + tax;

  return (
    <div className="payment-success-page">
      <h1>Order Confirmed for {name}</h1>
      <p>Your Order Is Being Processed</p>

      <div className="order-details">
        <div className="room-number">
          <p><strong>Room Number:</strong> {roomNumber}</p>
        </div>
        <div className="order-number">
          <p><strong>Order Code:</strong> {orderNumber}</p>
        </div>
      </div>

      <ul className="item-list">
        {cartItems.map(item => (
          <li key={item.id}>
            <span className="item-name">{item.name}</span>
            <span className="item-quantity"> x {item.quantity}</span>
            <span className="item-price"> Rp {item.price.toLocaleString()}</span>
          </li>
        ))}
      </ul>

      <div className="additional-charges">
        <div className="service-charge">
          <span> Service Charge 5% </span>
          <span> Rp {serviceCharge.toLocaleString()} </span>
        </div>
        <div className="tax">
          <span> Tax 10% </span>
          <span> Rp {tax.toLocaleString()} </span>
        </div>
        <div className="total-amount">
          <strong> Total </strong>
          <strong> Rp {total.toLocaleString()}</strong>
        </div>
      </div>

      <button onClick={goBackToCart}>
        Return To Menu
      </button>
    </div>
  );
};

export default PaymentSuccessfulPage;
