import React from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet } from 'react-native';

// Mock data
const DATA = [
  { id: '1', title: 'Cappuccino', price: 'Rp75.000', available: true },
  { id: '2', title: 'Americano', price: 'Rp73.000', available: false },
  // Add more items...
];

const MenuPage = ({ navigation }) => {
  // Item component
  const Item = ({ title, price, available }) => (
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
      <Text style={styles.price}>{price}</Text>
      {available ? (
        <TouchableOpacity style={styles.button}>
          <Text style={styles.buttonText}>Add to Cart</Text>
        </TouchableOpacity>
      ) : (
        <Text style={styles.soldOut}>Habis</Text>
      )}
    </View>
  );

  return (
    <FlatList
      data={DATA}
      renderItem={({ item }) => (
        <Item title={item.title} price={item.price} available={item.available} />
      )}
      keyExtractor={item => item.id}
    />
  );
};

const styles = StyleSheet.create({
  item: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
  title: {
    fontSize: 18,
  },
  price: {
    fontSize: 16,
    color: '#888',
  },
  button: {
    backgroundColor: '#007bff',
    padding: 10,
    borderRadius: 5,
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
  },
  soldOut: {
    color: 'red',
    fontWeight: 'bold',
  },
});

export default MenuPage;
