// PopUpCart.js
import React, {useMemo} from 'react';
import './PopUpCart.css'; // Create this CSS file for styling

const PopUpCart = ({ cartItems,  goToCart }) => {
  // You can include more logic to handle clicking on the cart

  const totalQuantity = useMemo(() => {return cartItems.reduce((total, item) => total + (item.quantity), 0)},[cartItems])


  return (
    <div className="floating-cart">


      <i className="fas fa-shopping-cart" onClick={goToCart}>View Shopping Cart</i> {/* Font Awesome cart icon */}
      <span className="item-count-badge">{totalQuantity}</span>

    </div>
  );
};

export default PopUpCart;
