// MenuItem.js
import React from 'react';

const MenuItem = ({ name, description, price, isAvailable, addToCart }) => {
  const handleClick = () => {
    if (isAvailable) {
      addToCart({ name, description, price });
    } else {
      alert('This item is currently unavailable.');
    }
  };

  return (
    <div className="menu-item">
      <div className="menu-item-details">
        <h2>{name}</h2>
        <p>{description}</p>
        <p>{price}</p>
      </div>
      <button onClick={handleClick} disabled={!isAvailable}>
        {isAvailable ? 'Add to Cart' : 'Habis'}
      </button>
    </div>
  );
};

export default MenuItem;
