// ProductPage.js
import React, { useState } from 'react';
import './ProductPage.css'

const ProductPage = ({ product, addToCart, goToMenu, globalExtras}) => {

  console.log(product, 'product is here')
  console.log(product.variants ? product.variants[0] : 'varriants here')
  console.log(product.variants[0].options,'options here?')

  const [selectedExtras, setSelectedExtras] = useState([]);

  const handleExtraChange = (extra, isChecked, option) => {
    if (option) {
      // Handling nested option selection
      if (isChecked) {
        setSelectedExtras(prev => [...prev, { ...extra, option: { ...option } }]);
      } else {
        setSelectedExtras(prev => prev.filter(e => !(e.id === extra.id && e.option.id === option.id)));
      }
    } else {
      // Simple extra selection
      if (isChecked) {
        setSelectedExtras(prev => [...prev, extra]);
      } else {
        setSelectedExtras(prev => prev.filter(e => e.id !== extra.id));
      }
    }
  };

  const handleAddToCart = () => {
    addToCart(product, selectedExtras);
    goToMenu(); // option to go back to navigation after adding to cart
  }




  return (
    <div className="product-page">
      <img src={product.imageName} alt={product.name} className="product-page-image" />
      <div className="product-details">
        <h1>{product.name}</h1>
        <p>{product.description}</p>
        <p>Price: Rp{product.price.toLocaleString()}</p>
      </div>

      <div className="extras-container">
        <h3>Additionals</h3>
        {globalExtras.map(extra => (

          <div key={extra.id}>
            <label>
              <input type="checkbox" checked={selectedExtras.includes(extra.id)} onChange={(e) => handleExtraChange(extra.id, e.target.checked)}/>
              {extra.name}
              (+Rp{extra.price})
            </label>

          </div>
        ))}
      </div>

      <div className="extras">
        <h3>Add Extras:</h3>
        {product.variants && product.variants.map(extra => (
          <div key={extra.id}>
            <label>
              <input
                type="checkbox"
                checked={selectedExtras.some(e => e.id === extra.id)}
                onChange={(e) => handleExtraChange(extra, e.target.checked)}
              />
              {extra.name} (+${extra.price})
            </label>
            {extra.options && extra.options.map(option => (
              <div key={option.id}>
                <label>
                  <input
                    type="radio"
                    name={extra.id}
                    value={option.id}
                    checked={selectedExtras.some(e => e.id === extra.id && e.option && e.option.id === option.id)}
                    onChange={(e) => handleExtraChange(extra, e.target.checked, option)}
                  />
                  {option.name} (+${option.price})
                </label>
              </div>
            ))}
          </div>
        ))}
      </div>

      <textarea
        placeholder="Catatan"
        maxLength="20"
      />

      <button onClick={handleAddToCart} className="add-to-cart-btn">Add to Cart</button>

      <button onClick={goToMenu} className="back-btn">Back To Menu</button>
    </div>
  );
};

export default ProductPage;
