import React, { useState } from 'react';
import './App.css';
import LandingPage from './LandingPage';
import MenuPage from './MenuPage';
import ProductPage from './ProductPage';
import CartPage from './CartPage';
import PaymentPage from './PaymentPage';
import PaymentSuccessfulPage from './PaymentSuccessfulPage';

function App() {
  const [currentPage, setCurrentPage] = useState('landing');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [cartItems, setCartItems] = useState([]);

  const foodData = [
    { id: 1, name: 'Cheeseburger', category: 'Burgers', description: 'Classic cheeseburger with lettuce, tomato, and cheese', price: 9.99 },
    { id: 2, name: 'French Fries', category: 'Sides', description: 'Crispy golden fries', price: 3.49 },
    { id: 3, name: 'Caesar Salad', category: 'Salads', description: 'Fresh romaine lettuce, Caesar dressing, croutons, and Parmesan cheese', price: 7.99 },
    // Add more food items here
  ];

  const getProductsByCategory = category => {
    return foodData.filter(item => item.category === category);
  };

  const goToMenu = () => setCurrentPage('menu');

  const showProducts = category => {
    setSelectedCategory(category);
    setCurrentPage('products');
  };

  const addToCart = product => {
    setCartItems([...cartItems, product]);
  };

  const removeFromCart = itemToRemove => {
    const updatedCartItems = cartItems.filter(item => item.id !== itemToRemove.id);
    setCartItems(updatedCartItems);
  };

  const goToCheckout = () => setCurrentPage('payment');

  const goBack = () => setCurrentPage('landing');

  const goToCart = () => setCurrentPage('cart');

  const handlePaymentSuccess = () => {
    setCurrentPage('paymentSuccessful');
  };

  return (
    <div className="App">
      {currentPage === 'landing' && <LandingPage goToMenu={goToMenu} />}
      {currentPage === 'menu' && (
        <MenuPage
          categories={['Burgers', 'Sides', 'Salads']}
          showProducts={showProducts}
          goBack={goBack}
        />
      )}
      {currentPage === 'products' && (
        <ProductPage
          category={selectedCategory}
          products={getProductsByCategory(selectedCategory)}
          addToCart={addToCart}
          goBackToMenu={goBack}
          goToCart={goToCart}
        />
      )}
      {currentPage === 'cart' && (
        <CartPage
          cartItems={cartItems}
          removeFromCart={removeFromCart}
          goToCheckout={goToCheckout}
          goBackToMenu={goBack}
        />
      )}
      {currentPage === 'payment' && (
        <PaymentPage
          cartItems={cartItems}
          goBackToCart={goToCart}
          handlePaymentSuccess={handlePaymentSuccess}
        />
      )}
      {currentPage === 'paymentSuccessful' && (
        <PaymentSuccessfulPage
          goBackToCart={goToCart}
        />
      )}
    </div>
  );
}

export default App;
