// CartPage.js
import React from 'react';
import './CartPage.css';

const CartPage = ({ cartItems, removeFromCart, goToCheckout, goBackToMenu, setCartItems }) => {

  const handleQuantityChange = (itemID, delta) => {
    const updatedCartItems = cartItems.map(item => {
      if (item.id === itemID) {
        const updatedQuantity = item.quantity + delta;
        return {...item, quantity: updatedQuantity >= 1 ? updatedQuantity : 1};
      }
      return item;
    });
    setCartItems(updatedCartItems);
  };

  const handleRemoveFromCart = (itemID) => {
    const updatedCartItems = cartItems.filter(item => item.id !== itemID);

    setCartItems(updatedCartItems);
  }




  // Function to calculate subtotal
  const calculateSubtotal = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price), 0);
  };

  return (
    <div className="cart-container">
      <h1>Shopping Cart</h1>
      <div className="cart-items">
        {cartItems.map(item => (
          <div key={item.id} className="cart-item">
            <img src={item.imageName} alt={item.name} className="item-image" />
            <div className="item-details">
              <h3>{item.name}</h3>  
              <p>Price: ${item.price}</p>
              <div className="quantity-control">
                <button onClick={() => handleQuantityChange(item.id,-1)}>-</button>
                <span>{item.quantity}</span>
                <button onClick={() => handleQuantityChange(item.id,1)}>+</button>
              </div>
              <button onClick={() => removeFromCart(item)}>Remove</button>
            </div>
          </div>
        ))}
      </div>
      <div>
        <p>Subtotal: ${calculateSubtotal().toFixed(2)}</p> {/* Display subtotal */}
        <button onClick={goToCheckout}>Proceed to Checkout</button>
        <button onClick={goBackToMenu}>Back to Menu</button>
      </div>
    </div>
  );
};

export default CartPage;
