import React from 'react';

const PaymentPage = ({ cartItems, goBackToCart, handlePaymentSuccess }) => {
  // Function to handle payment submission
  const handleSubmitPayment = () => {
    // Simulate payment processing logic
    // For simplicity, assume payment is always successful
    handlePaymentSuccess();
  };

  return (
    <div>
      <h1>Payment</h1>
      <div>
        {/* Display cart items */}
        <h2>Cart Items:</h2>
        <ul>
          {cartItems.map(item => (
            <li key={item.id}>
              {item.name} - ${item.price}
            </li>
          ))}
        </ul>
      </div>
      {/* Button to submit payment */}
      <button onClick={handleSubmitPayment}>Submit Payment</button>
      {/* Button to go back to cart */}
      <button onClick={goBackToCart}>Back to Cart</button>
    </div>
  );
};

export default PaymentPage;
