// MenuPage.js
import React, { useRef, useEffect } from 'react';
import './MenuPage.css';
import './CategoryContainer.css'
import CategoryContainer from './CategoryContainer';

const MenuPage = ({ categorizedFoodData, addToCart, goToCart, goBack }) => {

  const categoryRefs = useRef({});

  const setCategoryRef = (categoryName, element) => {if (element) categoryRefs[categoryName] = element};

  //const categoryRefs = Object.keys(categorizedFoodData).reduce((acc, category) => {acc[category] = useRef(null); return acc;}, {});

  const scrollToCategory = (categoryName) => {if (categoryRefs[categoryName]){categoryRefs[categoryName].scrollIntoView({behavior:'smooth',block:'start'})}};


  return (
    <div className="menu-page">

      <div className="categories-container">
        {Object.keys(categorizedFoodData).map(category => (
          <div key={category} className="category-item" onClick={() => scrollToCategory(category)}>
            {category}
          </div>
        ))}
      </div>

      {Object.keys(categorizedFoodData).map(category => (
        <div key={category} className="category-section">
          <h2>{category}</h2>
          <div className="products-grid">
            {categorizedFoodData[category].map(product => (
              <div key={product.id} className={`product-item ${!product.isAvailable ? 'product-item--unavailable' : ''}`}>
                {!product.isAvailable && <div className="product-overlay">Sold Out</div>}
                <img src={product.imageName} alt={product.name} className="product-image" />
                <div className="product-details">
                  <h3>{product.name}</h3>
                  <p>{product.description}</p>
                  <div className="price-and-cart">
                    <p className="product-price">Rp{product.price.toLocaleString('id-ID', { minimumFractionDigits: 0 })}</p>
                    {product.isAvailable && (
                     <button onClick={() => addToCart(product)} className="add-to-cart-button">
                        Add to Cart
                     </button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
      <button onClick={goToCart} className="view-cart-button">View Cart</button>
      <button onClick={goBack} className="back-button">Back to Landing Page</button>
    </div>
  );
};

export default MenuPage;
