// ProductPage.js
import React, { useState } from 'react';
import './ProductPage.css'

const ProductPage = ({ product, addToCart, goBack }) => {
  // const [variant, setVariant] = useState('');
  // const [quantity, setQuantity] = useState(1);
  // const [note, setNote] = useState('');

  // const handleVariantChange = (event) => {
  //   setVariant(event.target.value);
  // };

  // const handleQuantityChange = (change) => {
  //   setQuantity(prevQuantity => Math.max(1, prevQuantity + change));
  // };

  // const handleAddToCart = () => {
  //   addToCart({ ...product, variant, quantity, note });
  // };

  return (
    <div className="product-page">
      <img src={product.imageUrl} alt={product.name} className="product-image" />
      <h1>{product.name}</h1>
      <p>{product.description}</p>
      <p>Price: Rp{product.price.toLocaleString()}</p>

      {product.variants && (
        <div>
          <h3>{product.variants.title}</h3>
          {product.variants.options.map(option => (
            <label key={option}>
              <input
                type="radio"
                name="variant"
                value={option}
                checked={variant === option}
                onChange={handleVariantChange}
              />
              {option}
            </label>
          ))}
        </div>
      )}

      <textarea
        placeholder="Catatan"
        value={note}
        onChange={(e) => setNote(e.target.value)}
        maxLength="20"
      />

      <div className="quantity-selector">
        <button onClick={() => handleQuantityChange(-1)}>-</button>
        <span>{quantity}</span>
        <button onClick={() => handleQuantityChange(1)}>+</button>
      </div>

      <button onClick={handleAddToCart} className="add-to-cart-btn">
        Tambah ke keranjang
      </button>
      <button onClick={goBack} className="back-btn">Kembali</button>
    </div>
  );
};

export default ProductPage;
