// PopUpCart.js
import React, {useState, useEffect, useMemo} from 'react';
import './PopUpCart.css'; // Create this CSS file for styling

const PopUpCart = ({ cartItems,  goToCart }) => {

  const [isExpanded, setIsExpanded] = useState(false); // Controls the expanded state of the popup

  const handleToggleExpand = () => {
    setIsExpanded(!isExpanded);
  }



  const totalQuantity = useMemo(() => {return cartItems.reduce((total, item) => total + (item.quantity), 0)},[cartItems])


  return (
    <div className="floating-cart">

      <span className="item-count-badge">{totalQuantity}</span>
      <span className="fas fa-shopping-cart" onClick={handleToggleExpand}>{totalQuantity} dishes have been ordered</span> 

      {isExpanded ? (
              <div className="cart-details">
                  {cartItems.map((item, index) => (
                      <div key={index} className="cart-item">
                          <img src={item.imageName} alt={item.name} className="cart-item-image"/>
                          <div>
                              <h4>{item.name}</h4>
                              <p>${calculateItemSubtotal(item)} x {item.quantity}</p>
                          </div>
                      </div>
                  ))}
                  <div className="total">
                      <strong>Total: ${calculateTotal()}</strong>
                  </div>
                  <button onClick={goToCheckout}>Go to Checkout</button>
              </div>
          ) : (
              <div className="quick-summary">
                  <p>Items in Cart: {cartItems.length}</p>
                  <p>Total: ${calculateTotal()}</p>
              </div>
          )}

      <span>Settle Payment</span>


    </div>
  );
};

export default PopUpCart;
