// CartPage.js
import React, {useMemo} from 'react';
import './CartPage.css';

const CartPage = ({ cartItems, setCartItems, goToCheckout, goToMenu, updateItemQuantity }) => {
  // removeFromCart

  console.log(cartItems)

  const calculateSubtotal = useMemo(() => {return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0)},[cartItems])

  const handleIncrease = (itemID) => {
    const item = cartItems.find(item => item.id === itemID);
    updateItemQuantity(itemID, item.quantity + 1);
  }

  const handleDecrease = (itemId) => {
    // Adjust quantities in cartItems
    setCartItems(currentItems =>
      currentItems.reduce((acc, item) => {
        if (item.id === itemId) {
          if (item.quantity > 1) {
            // Decrease the quantity
            acc.push({ ...item, quantity: item.quantity - 1 });
          }
          // If quantity is 1, do not add the item back to acc, effectively removing it
        } else {
          // Add other items as is
          acc.push(item);
        }
        return acc;
      }, [])
    );
  };




  // Function to calculate subtotal
  // const calculateSubtotal = () => {
  //   return cartItems.reduce((total, item) => total + parseFloat(item.price), 0);
  // };

  return (
    <div className="cart-container">
      <h1>Shopping Cart</h1>
      <div className="cart-items">
        {cartItems.map(item => (

          <div key={item.id} className="cart-item">
            <img src={item.imageName} alt={item.name} className="item-image" />

            <div className="item-details">
              <h3>{item.name}</h3>  
              <p>Price: ${item.price}</p>

              <div className="quantity-control">
                <h3>{item.name}</h3>    
                <button onClick={() => handleDecrease(item.id,-1)}>-</button>
                <span>{item.quantity}</span>
                <button onClick={() => handleIncrease(item.id,1)}>+</button>
              </div>

            </div>

          </div>

        ))}
      </div>
      <div>
        <p>Subtotal: ${calculateSubtotal.toFixed(2)}</p> {/* Display subtotal */}
        <button onClick={goToCheckout}>Proceed to Checkout</button>
        <button onClick={goToMenu}>Back to Menu</button>
      </div>
    </div>
  );
};

export default CartPage;
