// App.js
import React, { useState } from 'react';
import { userRef } from 'react';
import './App.css';
import Header from './Header';
import LandingPage from './LandingPage';
import MenuPage from './MenuPage';
import BottomNavigation from './BottomNavigation';
import PaymentSuccessfulPage from './PaymentSuccessfulPage';
import CartPage from './CartPage';
import PaymentPage from './PaymentPage';
import ProductPage from './ProductPage'; // Import ProductPage component
import PopUpCart from './PopUpCart'; // Import the Popup component
import cheeseburgerImage from './images/cheese-burger.png'
import caesarsaladImage from './images/caesar-salad.png'
import chickenwingImage from './images/chicken-wing.png'
import friesImage from './images/fries.png'
import pizzaImage from './images/pizza.png'
import CategoryContainer from './CategoryContainer';

function App() {
  const [currentPage, setCurrentPage] = useState('landing');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [cartItems, setCartItems] = useState([]);
  const [showPopup, setShowPopup] = useState(false); // State to control popup visibility


  const foodData = [
    { id: 1, name: 'Cheeseburger', category: 'Burgers', description: 'Classic cheeseburger with lettuce, tomato, and cheese', price: 9.99, isAvailable: true, imageName: cheeseburgerImage },
    { id: 2, name: 'French Fries', category: 'Burgers', description: 'Crispy golden fries', price: 3.49, isAvailable: true, imageName: friesImage },
    { id: 3, name: 'Caesar Salad', category: 'Salads', description: 'Fresh romaine lettuce, Caesar dressing, croutons, and Parmesan cheese', price: 7.99, isAvailable: true, imageName: caesarsaladImage },
    { id: 4, name: 'Margherita Pizza', category: 'Pizzas', description: 'Classic pizza topped with tomato, mozzarella, and basil', price: 11.99, isAvailable: true, imageName: pizzaImage},
    { id: 5, name: 'Chicken Wings', category: 'Appetizers', description: 'Spicy chicken wings served with blue cheese dressing', price: 8.99, isAvailable: true, imageName: chickenwingImage },
    { id: 6, name: 'Spaghetti Bolognese', category: 'Pasta', description: 'Spaghetti pasta with meaty Bolognese sauce', price: 10.99, isAvailable: true },
    { id: 7, name: 'Grilled Salmon', category: 'Seafood', description: 'Perfectly grilled salmon with a lemon butter drizzle', price: 15.99, isAvailable: true },
    { id: 8, name: 'Vegetable Stir Fry', category: 'Vegetarian', description: 'A mix of seasonal vegetables stir-fried with soy sauce', price: 9.99, isAvailable: true },
    { id: 9, name: 'Beef Tacos', category: 'Mexican', description: 'Three beef tacos with salsa, cheese, and lettuce', price: 8.99, isAvailable: true },
    { id: 10, name: 'Chocolate Cake', category: 'Desserts', description: 'Rich chocolate cake with a molten chocolate center', price: 6.99, isAvailable: true },
  ];

  const categoryRefs = foodData.reduce((acc, category) => {acc[category.id] = useRef(); return acc;}, {});

  const handleCategoryClick = (categoryId) => {categoryRefs[categoryId].current.scrollIntoView({behavior:'smooth',block: 'start'})};


  // This function will group your food items by category
  const categorizedFoodData = foodData.reduce((acc, item) => {
    const { category, ...rest } = item;
    acc[category] = acc[category] || [];
    acc[category].push(rest);
    return acc;
  }, {});

  const getProductsByCategory = category => {
    return foodData.filter(item => item.category === category);
  };

  const showProducts = category => {
    setSelectedCategory(category);
    setCurrentPage('products');
  };

  const addToCart = product => {
    setCartItems([...cartItems, product]);
    setShowPopup(true); // Show the popup when an item is added

  };

  const handleClosePopup = () => {
    setShowPopup(false); // Hide the popup when the close button is clicked
  };

  const removeFromCart = itemToRemove => {
    const updatedCartItems = cartItems.filter(item => item.id !== itemToRemove.id);
    setCartItems(updatedCartItems);
  };

  const goToCheckout = () => setCurrentPage('payment');

  const goBack = () => setCurrentPage('landing');

  const goToCart = () => setCurrentPage('cart');

  const handlePaymentSuccess = () => {
    setCurrentPage('paymentSuccessful');
  };

  return (
    <div className="App">
      <Header title="Naked Papa" />
      {currentPage === 'landing' && <LandingPage goToMenu={() => setCurrentPage('menu')} />}
      {currentPage === 'menu' && (<MenuPage categorizedFoodData={categorizedFoodData} addToCart={addToCart} goToCart={goToCart} goBack={goBack} />)}
      {currentPage === 'products' && <ProductPage category={selectedCategory} products={getProductsByCategory(selectedCategory)} addToCart={addToCart} goBackToMenu={goBack} goToCart={goToCart} />}
      {currentPage === 'cart' && <CartPage cartItems={cartItems} removeFromCart={removeFromCart} goToCheckout={goToCheckout} goBackToMenu={goBack} />}
      {currentPage === 'payment' && <PaymentPage cartItems={cartItems} goBackToCart={goToCart} handlePaymentSuccess={handlePaymentSuccess} />}
      {currentPage === 'paymentSuccessful' && <PaymentSuccessfulPage goBackToCart={goToCart} />}
      {currentPage !== 'paymentSuccessful' && <BottomNavigation />}

      {/* Your existing components and JSX */}
      {showPopup && <PopUpCart count={cartItems.length} onClose={handleClosePopup} />}
    </div>
  );
}

export default App;
