// MenuPage.js
import React from 'react';
import './MenuPage.css'; // Make sure to create this CSS file

const MenuPage = ({ categories, foodData, showProducts, goBack }) => {
  return (
    <div className="menu-page">
      <h1>Menu</h1>
      <div className="categories">
        {categories.map((category, index) => (
          <button key={index} onClick={() => showProducts(category)}>{category}</button>
        ))}
      </div>
      <div className="products-grid">
        {foodData.map(product => (
          <div key={product.id} className={`product-item ${!product.isAvailable ? 'product-item--unavailable' : ''}`}>
            {!product.isAvailable && <div className="product-overlay">Habis</div>}
            <img src={product.imageUrl} alt={product.name} className="product-image" />
            <div className="product-details">
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p className="product-price">Rp{product.price.toLocaleString('id-ID', { minimumFractionDigits: 0 })}</p>
            </div>
          </div>
        ))}
      </div>
      <button onClick={goBack} className="back-button">Back to Landing Page</button>
    </div>
  );
};

export default MenuPage;
