// ProductPage.js
import React, { useState } from 'react';
import './ProductPage.css'

const ProductPage = ({ product, addToCart, goToMenu }) => {
  // const [variant, setVariant] = useState('');
  // const [quantity, setQuantity] = useState(1);
  // const [note, setNote] = useState('');

  // const handleVariantChange = (event) => {
  //   setVariant(event.target.value);
  // };


  if (!product) return <div>Loading...</div>

  return (
    <div className="product-page">
      <img src={product.imageName} alt={product.name} className="product-page-image" />
      <div className="product-details">
        <h1>{product.name}</h1>
        <p>{product.description}</p>
        <p>Price: Rp{product.price.toLocaleString()}</p>
      </div>


      {product.variants && (
        <div>
          <h3>{product.variants.title}</h3>
          {product.variants.options.map(option => (
            <label key={option}>
              <input
                type="radio"
                name="variant"
                value={option}
                checked={variant === option}
                onChange={handleVariantChange}
              />
              {option}
            </label>
          ))}
        </div>
      )}

      <textarea
        placeholder="Catatan"
        value={note}
        onChange={(e) => setNote(e.target.value)}
        maxLength="20"
      />

      <div className="quantity-selector">
        <button onClick={() => handleQuantityChange(-1)}>-</button>
        <span>{quantity}</span>
        <button onClick={() => handleQuantityChange(1)}>+</button>
      </div>

      <button onClick={() => addToCart(product)} className="add-to-cart-btn">
        Add To Cart
      </button>

      <button onClick={goToMenu} className="back-btn">Back To Menu</button>
    </div>
  );
};

export default ProductPage;
