import React, { useState } from 'react';
// import QRIS from './images'
// import './PaymentPage.css';

const PaymentPage = ({ cartItems, goBackToCart, handlePaymentSuccess }) => {
  // State customer details
  const [customerDetails, setCustomerDetails] = useState({
    name: '',
    phone: '',
    email: '',
    roomNumber: '',
  });

  // Calculate subtotal
  const subtotal = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  const handleDetailsChange = event => {
    const {name,value} = event.target;
    setCustomerDetails({...customerDetails,[name]:value})
  };



  // Handle input change for card details
  // const handleChange = event => {
  //   const { name, value } = event.target;
  //   setCardDetails({
  //     ...cardDetails,
  //     [name]: value
  //   });
  // };

  // Function to handle form submission
  const handleSubmit = event => {
    event.preventDefault();
    // Simulate payment processing logic
    // For simplicity, assume payment is always successful
    handlePaymentSuccess();
  };

  return (
    <div className="payment-page">
      <h1>Payment</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <h2>Cart Items:</h2>
          <ul>
            {cartItems.map(item => (
              <li key={item.id}>
                {item.name} - ${item.price} x {item.quantity}
              </li>
            ))}
          </ul>
          <p>Subtotal: ${subtotal.toFixed(2)}</p>
        </div>
        <div>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={customerInfo.name}
            onChange={handleInputChange}
            placeholder="Enter your name"
            required
          />
        </div>
        <div>
          <label htmlFor="roomNumber">Room Number:</label>
          <input
            type="text"
            id="roomNumber"
            name="roomNumber"
            value={customerInfo.roomNumber}
            onChange={handleInputChange}
            placeholder="Enter your room number"
            required
          />
        </div>
        <div className="payment-method">
          <h3>Scan to Pay</h3>
          <p>Your default payment method is QRIS. Please scan the QR code below to pay.</p>
          <img src={qrisImage} alt="QRIS Code" className="qris-code" />
        </div>
        <button type="submit" className="confirm-payment-button">Confirm Payment</button>
        <button type="button" onClick={goBackToCart} className="back-to-cart-button">Back to Cart</button>
      </form>
    </div>
  );
};

export default PaymentPage;
