// App.js
import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import LandingPage from './LandingPage';
import MenuPage from './MenuPage';
// import BottomNavigation from './BottomNavigation';
import PaymentSuccessfulPage from './PaymentSuccessfulPage';
import CartPage from './CartPage';
import PaymentPage from './PaymentPage';
import ProductPage from './ProductPage'; // Import ProductPage component
import PopUpCart from './PopUpCart'; // Import the Popup component
import cheeseburgerImage from './images/cheese-burger.png'
import caesarsaladImage from './images/caesar-salad.png'
import chickenwingImage from './images/chicken-wing.png'
import friesImage from './images/fries.png'
import pizzaImage from './images/pizza.png'


function App() {
  const [currentPage, setCurrentPage] = useState('landing');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [cartItems, setCartItems] = useState([]);
  const [showPopup, setShowPopup] = useState(false); // State to control popup visibility

  // passing name & roomNumber from paymentpage to paymentsuccessfulpage
  const [customerInfo, setCustomerInfo] = useState({name: '', roomNumber:''});
  const [orderDetails, setOrderDetails] = useState({roomNumber:'', name:'', orderNumber:''})


  const foodData = [
    { id: 1, name: 'Cheeseburger', category: 'Burgers', description: 'Classic cheeseburger with lettuce, tomato, and cheese', price: 9.99, isAvailable: true, imageName: cheeseburgerImage, quantity:1 },
    { id: 2, name: 'French Fries', category: 'Burgers', description: 'Crispy golden fries', price: 3.49, isAvailable: true, imageName: friesImage },
    { id: 3, name: 'Caesar Salad', category: 'Salads', description: 'Fresh romaine lettuce, Caesar dressing, croutons, and Parmesan cheese', price: 7.99, isAvailable: true, imageName: caesarsaladImage, quantity:1 },
    { id: 4, name: 'Margherita Pizza', category: 'Pizzas', description: 'Classic pizza topped with tomato, mozzarella, and basil', price: 11.99, isAvailable: true, imageName: pizzaImage, quantity:1},
    { id: 5, name: 'Chicken Wings', category: 'Appetizers', description: 'Spicy chicken wings served with blue cheese dressing', price: 8.99, isAvailable: true, imageName: chickenwingImage, quantity:1},
    { id: 6, name: 'Spaghetti Bolognese', category: 'Pasta', description: 'Spaghetti pasta with meaty Bolognese sauce', price: 10.99, isAvailable: true, quantity:1 },
    { id: 7, name: 'Grilled Salmon', category: 'Seafood', description: 'Perfectly grilled salmon with a lemon butter drizzle', price: 15.99, isAvailable: true, quantity:1},
    { id: 8, name: 'Vegetable Stir Fry', category: 'Vegetarian', description: 'A mix of seasonal vegetables stir-fried with soy sauce', price: 9.99, isAvailable: true, quantity:1},
    { id: 9, name: 'Beef Tacos', category: 'Mexican', description: 'Three beef tacos with salsa, cheese, and lettuce', price: 8.99, isAvailable: true, quantity:1 },
    { id: 10, name: 'Chocolate Cake', category: 'Desserts', description: 'Rich chocolate cake with a molten chocolate center', price: 6.99, isAvailable: true, quantity:1 },
  ];



  // This function will group your food items by category
  const categorizedFoodData = foodData.reduce((acc, item) => {
    const { category, ...rest } = item;
    acc[category] = acc[category] || [];
    acc[category].push(rest);
    return acc;
  }, {});

  const getProductsByCategory = category => {
    return foodData.filter(item => item.category === category);
  };

  const showProducts = category => {
    setSelectedCategory(category);
    setCurrentPage('products');
  };

  const addToCart = product => {
    // Check if the product already exists in the cart
    const existingItem = cartItems.find(item => item.id === product.id);

    if (existingItem) {
      // If it exists, update the quantity
      setCartItems(cartItems.map(item => 
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      ));
    } else {
      // If it doesn't exist, add the product to the cart with a quantity of 1
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }

    setShowPopup(true); // Show the popup when an item is added
  };

  const handleClosePopup = () => {
    setShowPopup(false); // Hide the popup when the close button is clicked
  };

  const updateItemQuantity = (itemID, quantity) => {
    setCartItems(currentItems => currentItems.map(item => item.id === itemID ?{ ...item, quantity:Math.max(1,quantity)} :item))
  }

  const removeFromCart = (itemId) => {
    setCartItems(currentItems => {
      // Find the item to see if we need to adjust quantity or remove it
      const existingItem = currentItems.find(item => item.id === itemId);
      if (existingItem && existingItem.quantity > 1) {
        // If item exists and quantity is greater than 1, decrease quantity
        return currentItems.map(item =>
          item.id === itemId ? { ...item, quantity: item.quantity - 1 } : item
        );
      } else {
        // If item exists and quantity is 1, remove it from the cart
        return currentItems.filter(item => item.id !== itemId);
      }
    });
  };


  const goToCheckout = () => setCurrentPage('payment');

  const goBack = () => setCurrentPage('landing');

  const goToCart = () => setCurrentPage('cart');

  // Function to change pages
  const goToProductPage = (selectedProduct) => {
    setSelectedProduct(selectedProduct); // Assuming you have a state to keep track of the selected product
    setCurrentPage('product');
  };

   // Render the appropriate page based on the current state
//    switch (currentPage) {
//     case 'menu':
//       return <MenuPage goToProductPage={goToProductPage} />;
//     case 'product':
//       return (
//         <ProductPage
//           category={selectedProduct.category}
//           product={selectedProduct}
//           addToCart={addToCart}
//           goBackToMenu={() => setCurrentPage('menu')}
//           goToCart={() => setCurrentPage('cart')}
//         />
//       );
//     // ... other cases ...
//     default:
//       // Default case can be your landing page or any other component
//       return <LandingPage />;
//   }
// }


  const handlePaymentSuccess = () => {
    const orderNumber = Math.random().toString(36).substring(2,15);

    console.log('Cust Info:', customerInfo);

    setOrderDetails({
      roomNumber: customerInfo.roomNumber,
      name: customerInfo.name,
      orderNumber: orderNumber
    });

    console.log('Ord Deets:', {roomNumber: customerInfo.roomNumber, name: customerInfo.name, orderNumber: orderNumber});

    setCurrentPage('paymentSuccessful');
  };

  return (
    <div className="App">
      <Header title="Naked Papa" />
      {currentPage === 'landing' && <LandingPage goToMenu={() => setCurrentPage('menu')} />}
      {currentPage === 'menu' && (<MenuPage categorizedFoodData={categorizedFoodData} addToCart={addToCart} goToCart={goToCart} goBack={goBack} />)}
      {currentPage === 'products' && <ProductPage category={selectedCategory} products={getProductsByCategory(selectedCategory)} addToCart={addToCart} goBackToMenu={goBack} goToCart={goToCart} />}
      {currentPage === 'cart' && <CartPage cartItems={cartItems} updateItemQuantity={updateItemQuantity} setCartItems={setCartItems} removeFromCart={removeFromCart} goToCheckout={goToCheckout} goBackToMenu={goBack} />}
      {currentPage === 'payment' && <PaymentPage cartItems={cartItems} customerInfo={customerInfo} setCustomerInfo={setCustomerInfo} goBackToCart={goToCart} handlePaymentSuccess={handlePaymentSuccess} />}
      {currentPage === 'paymentSuccessful' && <PaymentSuccessfulPage roomNumber={orderDetails.roomNumber} name={orderDetails.name} orderNumber={orderDetails.orderNumber} goBackToCart={goToCart} cartItems={cartItems} />}
      {/* {currentPage !== 'paymentSuccessful' && <BottomNavigation />} */}

      {/* Your existing components and JSX */}
      {showPopup && <PopUpCart cartItems={cartItems} onClose={handleClosePopup} />}

    </div>
  );
}

export default App;
