import React, { useState } from 'react';
import './App.css';
import LandingPage from './LandingPage';
import MenuPage from './MenuPage';
import ProductPage from './ProductPage';
import CartPage from './CartPage';
import PaymentPage from './PaymentPage';

function App() {
  const [currentPage, setCurrentPage] = useState('landing');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [cartItems, setCartItems] = useState([]);

  const goToMenu = () => setCurrentPage('menu');

  const showProducts = category => {
    setSelectedCategory(category);
    setCurrentPage('products');
    // Fetch products for the selected category from the server
    // Update products state
  };

  const addToCart = product => {
    setCartItems([...cartItems, product]);
  };

  const removeFromCart = itemToRemove => {
    const updatedCartItems = cartItems.filter(item => item.id !== itemToRemove.id);
    setCartItems(updatedCartItems);
  };

  const goToCheckout = () => setCurrentPage('payment');

  const goBack = () => setCurrentPage('landing');

  const goBackToMenu = () => setCurrentPage('menu');

  const goBackToCart = () => setCurrentPage('cart');

  return (
    <div className="App">
      {currentPage === 'landing' && <LandingPage goToMenu={goToMenu} />}
      {currentPage === 'menu' && (
        <MenuPage
          categories={['Drinks', 'Snacks', 'Desserts']} // Sample categories, replace with actual data
          showProducts={showProducts}
          goBack={goBack}
        />
      )}
      {currentPage === 'products' && (
        <ProductPage
          category={selectedCategory}
          products={[]} // Replace with actual products for the selected category
          addToCart={addToCart}
          goBackToMenu={goBackToMenu}
        />
      )}
      {currentPage === 'cart' && (
        <CartPage
          cartItems={cartItems}
          removeFromCart={removeFromCart}
          goToCheckout={goToCheckout}
          goBackToMenu={goBackToMenu}
        />
      )}
      {currentPage === 'payment' && (
        <PaymentPage
          cartItems={cartItems}
          goBackToCart={goBackToCart}
        />
      )}
    </div>
  );
}

export default App;
