// MenuPage.js
import React, { useRef, useEffect } from 'react';
import './MenuPage.css';

const MenuPage = ({ categorizedFoodData, goToCart, goBack, goToProductPage }) => {

  console.log(categorizedFoodData)

  const categoryRefs = useRef({});

  useEffect(() => {Object.keys(categorizedFoodData).forEach(category => {categoryRefs.current[category] = categoryRefs.current[category] || React.createRef()})}, [categorizedFoodData]);

  const scrollToCategory = (categoryName) => {const categoryRef = categoryRefs.current[categoryName]; if (categoryRef && categoryRef.current){categoryRef.current.scrollIntoView({behavior:'smooth',block:'start'})}}

  return (
    <div className="menu-page">

      <div className="menu-container">

        <div className="categories-container">
          {Object.keys(categorizedFoodData).map(category => (
            <div key={category} className="category-item" onClick={() => scrollToCategory(category)}>
              {category}
            </div>
          ))}
        </div>

        {Object.keys(categorizedFoodData).map(category => (
          <div key={category} ref={categoryRefs.current[category]} className="category-section">
            <h3>{category}</h3>

            <div className="products-grid-container"> 
              {categorizedFoodData[category].map(product => (

                <div key={product.id} className={`product-item ${!product.isAvailable ? 'product-item--unavailable' : ''}`}>
                  {!product.isAvailable && <div className="product-overlay">Sold Out</div>}
                  <div className="product-image-container">
                    <img src={product.imageName} alt={product.name} className="product-image" onClick={() => goToProductPage(product)} />
                  </div>
                  <div className="product-details">
                    <h3>{product.name}</h3>
                    <p>{product.description}</p>
                    <div className="price-and-cart">
                      <p className="product-price">Rp{product.price.toLocaleString('id-ID', { minimumFractionDigits: 0 })}</p>
                      {product.isAvailable && (
                      <button onClick={() => goToProductPage(product)} className="view-product-button">
                          View Product
                      </button>
                      )}
                    </div>
                  </div>

                </div>

              ))}
            </div>

          </div>
        ))}
        <button onClick={goToCart} className="view-cart-button">View Cart</button>
        <button onClick={goBack} className="back-button">Back to Landing Page</button>
      </div>
    </div>
  );
};

export default MenuPage;
