// PopUpCart.js
import React from 'react';
import './PopUpCart.css'; // Create this CSS file for styling

const PopUpCart = ({ count, onClose }) => {
  // You can include more logic to handle clicking on the cart

  return (
    <div className="floating-cart">

      <div>
        <span onClick={onClose}>X</span>  
      </div>

      <i className="fas fa-shopping-cart" onClick={onClose}>Shopping Cart</i> {/* Font Awesome cart icon */}
      <span className="item-count-badge">{count}</span>

    </div>
  );
};

export default PopUpCart;
