import React from 'react';

const MenuPage = ({ categories, showProducts, goBack }) => {
  // Mockup data for images and details
  const menuItems = [
    { id: 1, name: 'Cappuccino', price: 'Rp75.000', description: 'Classic cappuccino with frothy milk', image: 'https://via.placeholder.com/50' },
    { id: 2, name: 'Americano', price: 'Rp73.000', description: 'Strong black coffee with hot water', image: 'https://via.placeholder.com/50' },
    // Add more items...
  ];

  return (
    <div>
      <h1>Menu</h1>
      <div className="categories-container">
        {categories.map((category, index) => (
          <div key={category + index} className="category-item">
            {menuItems.map(item => (
              <div key={item.id} className="menu-item">
                <button onClick={() => showProducts(category)} className="category-button">
                  <img src={item.image} alt="Category" className="category-image" />
                  {category}
                </button>
                <div className="menu-details">
                  <h2>Menu Details</h2>
                  <p>Name: {item.name}</p>
                  <p>Price: {item.price}</p>
                  <p>Description: {item.description}</p>
                </div>
              </div>
            ))}
          </div>
        ))}
      </div>
      <button onClick={goBack}>Back to Landing Page</button>
    </div>
  );
};

export default MenuPage;
